    public boolean moveArmToPos(double armPos) {
        currentArmPos = armEncoder.getRaw();
        SmartDashboard.putNumber("arm encoder", currentArmPos);
        if (armEncoderPeriodic < armPos) {
            SmartDashboard.putBoolean("is first statement", true);
            if ((armPos - armEncoderPeriodic) > 10) {
                armMotor.set(-0.4); //inverted power
                return false;
            } else {
                //armMotor.set(-1); // orion
                armMotor.set(0); //sully
                return true;
            }
        } else if (armEncoderPeriodic > armPos) {
            SmartDashboard.putBoolean("is first statement", false);
            if ((armEncoderPeriodic - armPos) > 10) {
                SmartDashboard.putBoolean("passing through", true);
                //armMotor.set(1); //orion
                armMotor.set(0.4); //sully inverted power
                return false;
            } else {
                armMotor.set(0);
                return true;
            }
        } else {
            armMotor.set(0);
            return true;
        }
    }

    public double checkArmEncoder() {
        return armEncoder.getDistance();
    }

    public void resetArmEncoder() {
        armEncoder.reset();
    }

    public void moveArmManual(double direction) {
        double manualArmPos = armEncoder.getRaw();
        SmartDashboard.putNumber("moveArmManualEcoder", manualArmPos);
        if (manualArmPos > 550){
            armMotor.set(direction * 0.55);
        } else if (manualArmPos > 650){
            armMotor.set(direction * 0.50);
        } else{
            armMotor.set(direction * 0.6);
        }
    }

    public void armSmallMove(boolean goUp) {
        if (goUp) {
            armMotor.set(-0.32);
        } else {
            armMotor.set(0.2);
        }
    }

    public boolean moveHinge(double angle){
        double hingePos = hingeEncoder.getDistance();
        double angleValue = 45;
        if(angle == 45.0){
            if(hingePos < angleValue){
                hingeMotor.set(0.35);
                return false;
            } else if(hingePos > angleValue){
                hingeMotor.set(-0.35);
                return false;
            } else{
                hingeMotor.set(0);
                return true;
            }
        }else{
            return true;
        }
    }

